<?php if ( have_posts() ) :	while ( have_posts() ) :	the_post(); ?>	<article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>		<?php 		$post_format = get_post_format();		// Featured Image		if ( has_post_thumbnail() ) { 			$full_image_size = sp_post_thumbnail( 'full' );			$thumb = sp_post_thumbnail('page-featured-image');			$output = '<div class="page-featured-image"><a rel="prettyPhoto" href="' . $full_image_size . '"><img src="' . $thumb . '" /></a></div>';		}		// Video post		if ( $post_format == 'video' ) {			$video_url = get_post_meta($post->ID, 'sp_video_id', true);			$output = '<div class="page-featured-image">' . sp_add_video($video_url) . '</div>';		} 		// Gallery post		if ( $post_format == 'gallery' ){ 			$args = array(			   'post_type' => 'attachment',			   'numberposts' => -1,			   'post_mime_type' => 'image',			   'post_status' => null,			   'post_parent' => $post->ID,			   'order' 		 => 'ASC',			   'orderby' 	 => 'menu_order',			);			$output = '<div class="flexslider">';			$output .= '<ul class="slides">';			$attachments = get_posts( $args );			if ( $attachments ) {				foreach ( $attachments as $attachment ) {					$full_image_size = wp_get_attachment_image_src( $attachment->ID, 'full' );					$output .= '<li>';					$output .= '<a rel="wp-prettyPhoto[gallery]" href="' . $full_image_size[0] . '">';					$output .= wp_get_attachment_image( $attachment->ID, 'page-featured-image' );					$output .= '</a>';					if ($attachment->post_excerpt)						$output .= '<p class="flex-caption">' . $attachment->post_excerpt . '</p>';					$output .= '</li>';				}			}					$output .= '</ul>';			$output .= '</div>';				}		echo $output;		?>				<header class="entry-header">			<h1 class="entry-title">			<?php 				if (is_single()) { 					the_title(); 				} else { 			?>			<a href="<?php the_permalink(); ?>" title="<?php the_title(); ?>"><?php the_title(); ?></a>			<?php } ?>			</h1>			<div class="entry-meta"><?php sp_post_meta(); ?></div>		</header>		<div class="entry-content">			<?php the_content(); ?>		</div><!-- .entry-content -->	</article><!-- #post --><?php endwhile;else : ?>		<?php get_template_part('includes/error404'); ?>	<?php endif; ?>