<?php if ( have_posts() ) :	while ( have_posts() ) :	the_post(); ?>	<article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>		<?php if ( has_post_thumbnail() ) { 			$full_image_size = sp_post_thumbnail( 'full' );			$thumb = sp_post_thumbnail('page-featured-image');			$output = '<div class="page-featured-image"><a rel="prettyPhoto" href="' . $full_image_size . '"><img src="' . $thumb . '" /></a></div>';			echo $output;		} ?>		<header class="entry-header">			<h1 class="entry-title"><?php the_title(); ?></h1>		</header>		<div class="entry-content">			<?php the_content(); ?>		</div><!-- .entry-content -->	</article><!-- #post --><?php endwhile;else : ?>		<?php get_template_part('includes/error404'); ?>	<?php endif; ?>