<?php if ( have_posts() ) :	while ( have_posts() ) :	the_post(); ?>	<article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>		<?php 			$project_image_gallery = get_post_meta( $post->ID, 'sp_project_img_type', true ); 			if (!$project_image_gallery){				if ( has_post_thumbnail() ) { 					$full_image_size = sp_post_thumbnail( 'full' );					$thumb = sp_post_thumbnail('page-featured-image');					$output = '<div class="page-featured-image"><a rel="wp-prettyPhoto[gallery]" href="' . $full_image_size . '"><img src="' . $thumb . '" /></a></div>';				}			} else {				$project_gallery = rwmb_meta( 'sp_project_gallery', $args = array('type' => 'plupload_image', 'size' => 'page-featured-image') ); 								$output = '<div class="flexslider">';				$output .= '<ul class="slides">';				foreach ( $project_gallery as $image ){					$output .= '<li>';					$output .= '<a rel="wp-prettyPhoto[gallery]" href="' . $image['full_url'] . '">';					$output .= '<img src="' . $image["url"] . '" />';					$output .= '</a>';					$output .= '</li>';				}				$output .= '</ul>';				$output .= '</div>';			}			echo $output;		?>		<header class="entry-header">			<h1 class="entry-title"><?php the_title(); ?></h1>		</header>		<div class="entry-content">			<?php the_content(); ?>		</div><!-- .entry-content -->	</article><!-- #post --><?php endwhile;else : ?>		<?php get_template_part('includes/error404'); ?>	<?php endif; ?>